import React from 'react';
import { useSpring, animated } from 'react-spring'
function Transitionlearn(props) {
  let fade = useSpring({
    from: {
      opacity: 0,
      myName : ''
    },
    opacity: 1,
    myName: props.title
  })
  return (
    <animated.div style={fade}>
      <h1>{props.title}</h1>
    </animated.div>
  );
}

export default Transitionlearn;
