import React from 'react';
import { useSpring, animated } from 'react-spring'
function Transitionlearn(props) {
  const Fade = useSpring({
    from: {
      opacity: 0
    },
    to: {
      opacity: 1
    },
    config: {
      delay: 1000
    }
  })
  return (
    <animated.div style={Fade}>
      <h1>Hello world</h1>
    </animated.div>
  );
}

export default Transitionlearn;
