import React from 'react';
import { useSpring, animated } from 'react-spring/renderprops'
import SimpleExample from './SimpleMap'
function Weather(props) {
  function calcCelsius(f) {
    let Celsius = (f - 32) * 5 / 9;
    return Celsius
  }

  return (
    <React.Fragment>
      <div className="details">
        <div className="detail1">
          <h3><span className="icon icon-location"></span>{props.city}</h3>
          <p>Showing weather for {props.weather.name}</p>
          <h4>{weather[0].main}</h4>
          <p>{weather[0].description}</p>
          <p>Coord: <br /> Lon {coord.lon} and Lat {coord.lat}</p>
        </div>
        <div className="detail2">
          <h3><span className="icon icon-temp"></span>Temperatures</h3>
          <h4>Main {calcCelsius(main.temp)} </h4>
          <h4>Max {calcCelsius(main.temp_max)} </h4>
          <h4>Min {calcCelsius(main.temp_min)} </h4>
          <h4>Feels like {calcCelsius(main.temp_min)} </h4>
          <h4>Humidity {main.humidity}% </h4>
        </div>
      </div>
      <SimpleExample zoom={props.zoom} view={props.view} coord={coord} />
    </React.Fragment>
  );
}

export default Weather;
