import React from 'react';
// import {Spring, animated, transition} from 'react-spring'
import './App.css';
import Weather from './Weather'
class App extends React.Component {
  constructor() {
    super();
    this.state = {
      api: 'http://api.openweathermap.org/data/2.5/weather?q=',
      apiKey: 'd3c8c876ccc60478a8b2beb0fec1ea7f',
      load: false,
      title: 'Weather App',
      city: 'Bucharest',
      lang: 'RO',
      wheater: ''
    }
    this.getWeather = this.getWeather.bind(this)
  }
  componentDidMount() {
    let request = (this.state.api + this.state.city + '&appid=' + this.state.apiKey + '&lang=' + this.state.lang)
    fetch(request)
      .then(resp => resp.json())
      .then(res => {
        this.setState({
          weather: res
        })
      })
    console.log(request)
  }
  getWeather() {
    this.setState({
      load: true
    })
  }
  handleInput(e) {
    console.log(e)
  }
  render() {
    return (
      <div className="app">
        <div className="header">
          <div className="container">
            <h1>{this.state.title}</h1>
            <p>Powered by T.Valentin</p>
          </div>
        </div>
        <div className="body">
          <div className="container">
            <label htmlFor="city">
            </label>
            <h2>Body</h2>
            <input onInput={() => this.handleInput} placeholder="City" id="city" name="city" type="text" />
            <button onClick={this.getWeather}>Weather for</button>
            {this.state.load ? <Weather weather={this.state.weather} city={this.state.city} /> : ''}
          </div>
        </div>
        <div className="footer">
          <div className="container">
            <h3>Weather App v1 Â© T.Valentin</h3>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
