  constructor() {
    super();
    this.state = {
      load: false,
      title: 'Weather App',
      city: 'Bucharest'
    }
    this.getWeather = this.getWeather.bind(this)
  }
  getWeather() {

  }
  render() {
    return (
      <div className="app">
        <div className="header">
          <div className="container">
            <h1>{this.state.title}</h1>
            <p>Powered by T.Valentin</p>
          </div>
        </div>
        <div className="body">
          <div className="container">
            <label htmlFor="city">
            </label>
            <h2>Body</h2>
            <input placeholder="City" id="city" name="city" type="text" />
            <button onClick={this.getWeather}>Weather for</button>
            {this.state.load ? <Weather city={this.state.city} /> : ''}
          </div>
        </div>
        <div className="footer">
          <div className="container">
            <h3>Weather App v1 Â© T.Valentin</h3>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
