import React from 'react';
// import {Spring, animated, transition} from 'react-spring'
import './App.css';
import Weather from './Weather'
import SimpleExample from './SimpleMap'
class App extends React.Component {
  constructor() {
    super();
    this.state = {
      api: 'http://api.openweathermap.org/data/2.5/weather?q=',
      apiKey: 'd3c8c876ccc60478a8b2beb0fec1ea7f',
      load: false,
      title: 'Weather App',
      city: 'Bucharest',
      lang: 'RO',
      wheater: '',
      long :'',
      lat: '',
    }
    this.getWeather = this.getWeather.bind(this)
    this.handleInput = this.handleInput.bind(this)
    this.fetchData = this.fetchData.bind(this)
    this.onViewportChanged = this.onViewportChanged.bind(this)
  }
  async fetchData(mod) {
    let request
    switch (mod) {
      case 2:
        request = (this.state.api + '&appid=' + this.state.apiKey + '&lang=' + this.state.lang + '&lon' + this.state.long + '&lat=' + this.state.lat)
        return 
        default:
        request = (this.state.api + this.state.city + '&appid=' + this.state.apiKey + '&lang=' + this.state.lang)
        break
    }
    console.log(request)
    const response = await fetch(request)
    const json = await response.json();
    this.setState({
      weather: json,
      load: true
    })
  }
  componentDidMount() {
    this.fetchData(1);
  }
  getWeather() {
    this.setState({
      load: false
    })
    this.fetchData(1);
    if (this.state.weather.cod === 200) {
      this.setState({
        load: true
      })
    } else {
      alert("Unknown city" + this.state.weather.cod)
    }
  }
  handleInput(e) {
    this.setState({
      [e.target.name]: e.target.value
    })
  }
  onViewportChanged(viewport) {
    let { center } = viewport
    let [lat, long] = center
    console.log(lat, long)
    this.setState({
      lat: lat,
      long: long
    })
    this.fetchData(2)
  }
  render() {
    return (
      <div className="app">
        <div className="header">
          <div className="container">
            <h1>{this.state.title}</h1>
            <p>Powered by T.Valentin</p>
            <select onChange={this.handleInput} name="lang">
              <option value="RO">Romana</option>
              <option value="EN">English</option>
              <option value="RU">Russian</option>
            </select>
          </div>
        </div>
        <div className="body">
          <div className="container">
            <label htmlFor="city">
            </label>
            <h2>Body</h2>
            <input onChange={(e) => this.handleInput(e)} placeholder="City" id="city" name="city" type="text" />
            <button onClick={this.getWeather}>Get Weather</button>
            {this.state.load ? <Weather view={this.onViewportChanged} weather={this.state.weather} city={this.state.city} /> : ''}
          </div>
        </div>
        <div className="footer">
          <div className="container">
            <h3>Weather App v1 Â© T.Valentin</h3>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
